struct Menu{ 

    enum Armor{
        None = 0,
        Light,
        Medium,
        Heavy
    };
    

    enum Melee_Weapons{
        Saw_Cleaver = 4,
        Hunter_Axe,
        Threaded_Cane
    };

    enum Ranged_Weapons{
        Hunter_Pistol = 7,
        Hunter_Blunderbuss
    };


};

istream& operator>>(std::istream& inAI, Menu::Armor& armor) {

    //Ask The User for Their Armor Type Then Gets The Input
    while(true) {
    string armor_input;

    cout << "Select An Armor Type (None, Light, Medium, or Heavy): ";
    inAI >> armor_input;

    if (armor_input == "None") {
      armor = Menu::None;
      break;
    }
    else if (armor_input == "Light") {
      armor = Menu::Light;
      break;
    }
    else if (armor_input == "Medium") {
      armor = Menu::Medium;
      break;
    }
    else if (armor_input == "Heavy") {
      armor = Menu::Heavy;
      break;
    }
    else {
      cout << "Invalid Input! Please Select One of the Four Choices (P.S it's Case-Sensitive RN)" << endl;
      continue;
    }
  }
    return inAI;
}

